import React from "react";
import { Typography, List, ListItem, ListItemText, Stack } from "@mui/material";

const QuestionNo = ({
  questions,
  handleQuestionClick,
  currentQuestionIndex,
  attemptedQuestions,
  visitedQuestions,
}) => {
  const getBackgroundColor = (questionIndex) => {
    if (attemptedQuestions.includes(questionIndex)) {
      return "#007bff"; // Attempted questions are blue
    } else if (visitedQuestions.includes(questionIndex)) {
      return "#f7a90d"; // Visited questions are yellow
    } else {
      return "#00000073"; // Not visited questions are white
    }
  };

  const renderQuestionList = () => {
    return questions.map((question, questionIndex) => (
      <ListItem
        key={questionIndex}
        onClick={() => handleQuestionClick(questionIndex)}
        button
        selected={currentQuestionIndex === questionIndex}
        style={{
          width: "50px",
          margin: "4px",
          textAlign: "center",
          borderRadius: "0px 10px 0px 0px",
          backgroundColor: getBackgroundColor(questionIndex),
        }}
      >
        <ListItemText
          primary={`${questionIndex + 1}`}
          style={{ color: "#fff" }}
        />
      </ListItem>
    ));
  };

  return (
    <>
      <div>
        <Typography
          variant="h6"
          style={{ textAlign: "center", marginBottom: "8px" }}
        >
          Questions No.
        </Typography>
        <hr />
        <List
          style={{
            display: "flex",
            flexWrap: "wrap",
            justifyContent: "center",
          }}
        >
          {renderQuestionList()}
        </List>
      </div>
      <hr />
      <Stack direction="row" justifyContent="space-between">
        {[
          { backgroundColor: "#00000073", label: "Not Visited" },
          { backgroundColor: "#f7a90d", label: "Visited" },
          { backgroundColor: "#007bff", label: "Attempted" },
        ].map((item, index) => (
          <div style={{ display: "flex", alignItems: "center" }} key={index}>
            <div
              style={{
                width: "20px",
                height: "20px",
                backgroundColor: item.backgroundColor,
                borderRadius: "50%",
                marginRight: "4px",
              }}
            />
            <Typography style={{ fontSize: "16px" }}>{item.label}</Typography>
          </div>
        ))}
      </Stack>
    </>
  );
};

export default QuestionNo;
