import React, { useState } from "react";
import {
  Button,
  Container,
  TextField,
  Typography,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
} from "@mui/material";

const StartPage = ({ onStart }) => {
  const [email, setEmail] = useState("");
  const [isDialogOpen, setDialogOpen] = useState(false); // State for the dialog

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isValidEmail(email)) {
      onStart(email);
    } else {
      // Show the dialog if the email is invalid or empty
      setDialogOpen(true);
    }
  };

  const isValidEmail = (email) => {
    // Add your email validation logic here
    // For a basic example, you can check if it contains an "@" symbol
    return email.includes("@");
  };

  const handleCloseDialog = () => {
    setDialogOpen(false);
  };

  return (
    <Container maxWidth="sm">
      <Typography variant="h4" gutterBottom>
        Welcome to the Quiz!
      </Typography>
      <form onSubmit={handleSubmit}>
        <TextField
          fullWidth
          label="Email"
          type="email"
          variant="outlined"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          margin="normal"
        />
        <Button type="submit" variant="contained" color="primary">
          Start Quiz
        </Button>
      </form>
      {/* Dialog for invalid email */}
      <Dialog open={isDialogOpen} onClose={handleCloseDialog}>
        <DialogTitle>Error</DialogTitle>
        <DialogContent>Please enter a valid email address.</DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog} color="primary" autoFocus>
            OK
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default StartPage;
