import React, { useState } from "react";
import StartPage from "./components/StartPage";
import QuizComponent from "./components/QuizComponent";
import Timer from "./components/Timer";
import QuizResults from "./components/QuizResults"; // Import the QuizResults component

const App = () => {
  const [showQuiz, setShowQuiz] = useState(false);
  const [email, setEmail] = useState("");
  const [quizLoaded, setQuizLoaded] = useState(false);
  const [quizTimerActive, setQuizTimerActive] = useState(false);
  const [quizTimeout, setQuizTimeout] = useState(false); // New state for quiz timeout

  const startQuiz = (userEmail) => {
    setEmail(userEmail);
    setShowQuiz(true);
    setQuizTimerActive(true);

    // Set a timeout to mark the quiz as timed out after 60 seconds
    setTimeout(() => {
      setQuizTimeout(true);
      setQuizTimerActive(false); // Stop the quiz timer when it times out
    }, 60000); // 60 seconds in milliseconds
  };

  const handleTimeout = () => {
    setShowQuiz(false);
    setQuizTimerActive(false);
    alert("Quiz time is up! Submitting quiz.");
  };

  const stopQuizTimer = () => {
    setQuizTimerActive(false);
  };

  const handleQuizLoaded = () => {
    setQuizLoaded(true);
  };

  return (
    <div>
      {!showQuiz ? (
        <StartPage onStart={startQuiz} />
      ) : (
        <>
          <h1>Quiz for {email}</h1>
          {quizTimerActive && <Timer duration={60} onTimeout={handleTimeout} />}
          {quizTimeout ? (
            <QuizResults /> // Render QuizResults when the quiz times out
          ) : (
            <QuizComponent
              onLoaded={handleQuizLoaded}
              stopQuizTimer={stopQuizTimer}
            />
          )}
          {quizLoaded && quizTimerActive && (
            <Timer duration={60} onTimeout={handleTimeout} />
          )}
        </>
      )}
    </div>
  );
};

export default App;
