import React, { useState, useEffect } from "react";
import {
  Typography,
  List,
  ListItem,
  ListItemText,
  Checkbox,
  FormControlLabel,
  Container,
  Button,
  Paper,
  Stack,
  Box,
} from "@mui/material";

import { fetchQuizQuestions } from "../data/api";

const QuizComponent = () => {
  const [questions, setQuestions] = useState([]);
  const [selectedAnswers, setSelectedAnswers] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [visitedQuestions, setVisitedQuestions] = useState([]);
  const [attemptedQuestions, setAttemptedQuestions] = useState([]);

  useEffect(() => {
    const fetchQuestions = async () => {
      const quizQuestions = await fetchQuizQuestions();
      const initialSelectedAnswers = new Array(quizQuestions.length).fill(null);
      setSelectedAnswers(initialSelectedAnswers);
      setQuestions(quizQuestions);
    };

    fetchQuestions();

    // Mark the first question as visited when the questions are loaded
    if (questions.length > 0) {
      setVisitedQuestions([0]);
    }
  }, []);

  const handleAnswerSelect = (questionIndex, answerIndex) => {
    const newSelectedAnswers = [...selectedAnswers];
    newSelectedAnswers[questionIndex] = answerIndex;
    setSelectedAnswers(newSelectedAnswers);

    // Add the question to attempted questions
    setAttemptedQuestions([...attemptedQuestions, questionIndex]);
  };

  const handleQuestionClick = (questionIndex) => {
    setCurrentQuestionIndex(questionIndex);

    // Mark the question as visited
    if (!visitedQuestions.includes(questionIndex)) {
      setVisitedQuestions([...visitedQuestions, questionIndex]);
    }
  };

  const renderOverviewPanel = () => {
    return (
      <div>
        <Typography variant="h5">Questions</Typography>
        <List style={{ display: "flex", flexWrap: "wrap" }}>
          {questions.map((question, questionIndex) => (
            <ListItem
              key={questionIndex}
              onClick={() => handleQuestionClick(questionIndex)}
              button
              selected={currentQuestionIndex === questionIndex}
              style={{
                width: "50px",
                margin: "4px",
                textAlign: "center",
                borderRadius: "0px 10px 0px 0px",
                backgroundColor: attemptedQuestions.includes(questionIndex)
                  ? "#007bff" // Attempted questions are blue
                  : visitedQuestions.includes(questionIndex)
                  ? "#f7a90d" // Visited questions are yellow
                  : "#00000073", // Not visited questions are white
              }}
            >
              <ListItemText
                primary={`${questionIndex + 1}`}
                style={{
                  color: "#fff",
                }}
              />
            </ListItem>
          ))}
        </List>
      </div>
    );
  };

  const currentQuestion = questions[currentQuestionIndex];

  return (
    <Container maxWidth="lg">
      <Box display="flex" justifyContent="space-between">
        {/* Overview Panel */}
        <Paper
          elevation={3}
          style={{
            padding: "20px",
            marginRight: "20px",
            width: "30%",
            backgroundColor: "#f0f0f0",
          }}
        >
          {renderOverviewPanel()}
          <hr></hr>
          <Stack direction="row" justifyContent="space-around">
            <div style={{ display: "flex", alignItems: "center" }}>
              <div
                style={{
                  width: "20px",
                  height: "20px",
                  backgroundColor: "#00000073",
                  borderRadius: "50%",
                  marginRight: "6px",
                }}
              />
              <Typography style={{ fontSize: "16px" }}>Not Visited</Typography>
            </div>
            <div style={{ display: "flex", alignItems: "center" }}>
              <div
                style={{
                  width: "20px",
                  height: "20px",
                  backgroundColor: "#f7a90d",
                  borderRadius: "50%",
                  marginRight: "6px",
                }}
              />
              <Typography style={{ fontSize: "16px" }}> Visited</Typography>
            </div>
            <div style={{ display: "flex", alignItems: "center" }}>
              <div
                style={{
                  width: "20px",
                  height: "20px",
                  backgroundColor: "#007bff",
                  borderRadius: "50%",
                  marginRight: "6px",
                }}
              />
              <Typography style={{ fontSize: "16px" }}>Attempted</Typography>
            </div>
          </Stack>
        </Paper>

        <div>
          <Paper
            elevation={3}
            style={{
              padding: "20px",
              width: "70%",
              height: "fit-content",
              backgroundColor: "#ffffff",
            }}
          >
            <List>
              <ListItem>
                <div
                  style={{
                    display: "flex",
                    flexDirection: "column",
                  }}
                >
                  <ListItemText primary={`${currentQuestion?.question}`} />
                  {currentQuestion?.options.map((choice, choiceIndex) => (
                    <FormControlLabel
                      key={choiceIndex}
                      control={
                        <Checkbox
                          checked={
                            selectedAnswers[currentQuestionIndex] ===
                            choiceIndex
                          }
                          onChange={() =>
                            handleAnswerSelect(
                              currentQuestionIndex,
                              choiceIndex
                            )
                          }
                        />
                      }
                      label={choice}
                    />
                  ))}
                </div>
              </ListItem>
            </List>
          </Paper>
          <Button
            variant="contained"
            color="primary"
            style={{ marginTop: "20px", backgroundColor: "#007bff" }} // Example button style
          >
            Submit Answers
          </Button>
        </div>
      </Box>

      {/* Submit Button */}
    </Container>
  );
};

export default QuizComponent;
