import React, { useState, useEffect } from "react";
import {
  Typography,
  List,
  ListItem,
  ListItemText,
  Radio,
  FormControlLabel,
  Container,
  Button,
  Paper,
  RadioGroup,
  Box,
} from "@mui/material";

import { fetchQuizQuestions } from "../data/api";
import QuestionNo from "./QuestionNo";

const QuizComponent = () => {
  const [questions, setQuestions] = useState([]);
  const [selectedAnswers, setSelectedAnswers] = useState(
    new Array(questions.length).fill(null)
  );
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [visitedQuestions, setVisitedQuestions] = useState([]);
  const [attemptedQuestions, setAttemptedQuestions] = useState([]);

  useEffect(() => {
    const fetchQuestions = async () => {
      const quizQuestions = await fetchQuizQuestions();
      setQuestions(quizQuestions);
      // Initialize selectedAnswers with the same length as the questions array
      setSelectedAnswers(new Array(quizQuestions.length).fill(null));
    };

    fetchQuestions();
  }, []);

  const handleAnswerSelect = (questionIndex, answerIndex) => {
    const newSelectedAnswers = [...selectedAnswers];
    newSelectedAnswers[questionIndex] = answerIndex;
    setSelectedAnswers(newSelectedAnswers);

    if (!attemptedQuestions.includes(questionIndex)) {
      setAttemptedQuestions([...attemptedQuestions, questionIndex]);
    }
  };
  const handleQuestionClick = (questionIndex) => {
    setCurrentQuestionIndex(questionIndex);

    if (!visitedQuestions.includes(questionIndex)) {
      setVisitedQuestions([...visitedQuestions, questionIndex]);
    }
  };

  const currentQuestion = questions[currentQuestionIndex];

  return (
    <Container maxWidth="lg">
      <Box display="flex" justifyContent="space-between">
        {/* Overview Panel */}
        <Paper
          elevation={3}
          style={{
            padding: "20px",
            marginRight: "20px",
            width: "30%",
            backgroundColor: "#f0f0f0",
          }}
        >
          <QuestionNo
            questions={questions}
            handleQuestionClick={handleQuestionClick}
            currentQuestionIndex={currentQuestionIndex}
            attemptedQuestions={attemptedQuestions}
            visitedQuestions={visitedQuestions}
          />
        </Paper>

        <Paper
          elevation={3}
          style={{
            padding: "20px",
            width: "70%",
            height: "fit-content",
            backgroundColor: "#ffffff",
          }}
        >
          <Typography variant="h5">Quiz Questions</Typography>
          <List>
            <ListItem>
              <div>
                <ListItemText primary={currentQuestion?.question} />
                <RadioGroup
                  name={`question-${currentQuestionIndex}`}
                  value={selectedAnswers[currentQuestionIndex]}
                  onChange={(e) =>
                    handleAnswerSelect(currentQuestionIndex, e.target.value)
                  }
                >
                  {currentQuestion?.incorrect_answers.map(
                    (choice, choiceIndex) => (
                      <FormControlLabel
                        key={choiceIndex}
                        value={choiceIndex}
                        control={<Radio />}
                        label={choice}
                      />
                    )
                  )}
                </RadioGroup>
              </div>
            </ListItem>
          </List>
        </Paper>
      </Box>

      {/* Submit Button */}
      <Button
        variant="contained"
        color="primary"
        style={{ marginTop: "20px", backgroundColor: "#007bff" }}
      >
        Submit Answers
      </Button>
    </Container>
  );
};

export default QuizComponent;
