import React, { useState, useEffect } from "react";

import { fetchQuizQuestions } from "../data/api";

const QuizComponent = () => {
  const [questions, setQuestions] = useState([]);
  const [selectedAnswers, setSelectedAnswers] = useState([]);

  useEffect(() => {
    const fetchQuestions = async () => {
      const quizQuestions = await fetchQuizQuestions();
      const initialSelectedAnswers = new Array(quizQuestions.length).fill(null);
      setSelectedAnswers(initialSelectedAnswers);
      setQuestions(quizQuestions);
    };

    fetchQuestions();
  }, []);

  const handleAnswerSelect = (questionIndex, answerIndex) => {
    const newSelectedAnswers = [...selectedAnswers];
    newSelectedAnswers[questionIndex] = answerIndex;
    setSelectedAnswers(newSelectedAnswers);
  };

  return (
    <div>
      <h1>Quiz Questions</h1>
      <ul>
        {questions.map((question, questionIndex) => (
          <li key={questionIndex}>
            <p>{question.question}</p>
            <ul>
              {question.incorrect_answers.map((choice, choiceIndex) => (
                <li key={choiceIndex}>
                  <label>
                    <input
                      type="radio"
                      name={`question-${questionIndex}`}
                      checked={selectedAnswers[questionIndex] === choiceIndex}
                      onChange={() =>
                        handleAnswerSelect(questionIndex, choiceIndex)
                      }
                    />
                    {choice}
                  </label>
                </li>
              ))}
            </ul>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default QuizComponent;
