import React from "react";
import {
  ListItem,
  ListItemText,
  FormControlLabel,
  Checkbox,
  List,
} from "@mui/material";

const Question = ({ question, selectedAnswer, onAnswerSelect }) => {
  return (
    <div>
      <ListItemText primary={question.question} />

      <List>
        {question.options.map((choice, choiceIndex) => (
          <ListItem key={choiceIndex}>
            <FormControlLabel
              control={
                <Checkbox
                  checked={selectedAnswer === choiceIndex}
                  onChange={() => onAnswerSelect(choiceIndex)}
                />
              }
              label={choice}
            />
          </ListItem>
        ))}
      </List>
    </div>
  );
};

export default Question;
