import React, { useState, useEffect } from "react";
import {
  Typography,
  List,
  ListItem,
  ListItemText,
  Radio,
  FormControlLabel,
  Container,
  Button,
  Box,
  Paper,
} from "@mui/material";

import { fetchQuizQuestions } from "../data/api";

const QuizComponent = () => {
  const [questions, setQuestions] = useState([]);
  const [selectedAnswers, setSelectedAnswers] = useState([]);

  useEffect(() => {
    const fetchQuestions = async () => {
      const quizQuestions = await fetchQuizQuestions();
      const initialSelectedAnswers = new Array(quizQuestions.length).fill(null);
      setSelectedAnswers(initialSelectedAnswers);
      setQuestions(quizQuestions);
    };

    fetchQuestions();
  }, []);

  const handleAnswerSelect = (questionIndex, answerIndex) => {
    const newSelectedAnswers = [...selectedAnswers];
    newSelectedAnswers[questionIndex] = answerIndex;
    setSelectedAnswers(newSelectedAnswers);
  };

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: "20px" }}>
        <Typography variant="h5">Quiz Questions</Typography>
        <List>
          {questions.map((question, questionIndex) => (
            <ListItem key={questionIndex}>
              <ListItemText primary={question.question} />
              <RadioGroup
                name={`question-${questionIndex}`}
                value={selectedAnswers[questionIndex]}
                onChange={(e) =>
                  handleAnswerSelect(questionIndex, e.target.value)
                }
              >
                {question.incorrect_answers.map((choice, choiceIndex) => (
                  <FormControlLabel
                    key={choiceIndex}
                    value={choiceIndex}
                    control={<Radio />}
                    label={choice}
                  />
                ))}
              </RadioGroup>
            </ListItem>
          ))}
        </List>
        <Box mt={2} display="flex" justifyContent="center">
          <Button variant="contained" color="primary">
            Submit Answers
          </Button>
        </Box>
      </Paper>
    </Container>
  );
};

export default QuizComponent;
