import React from "react";
import {
  Typography,
  List,
  ListItem,
  ListItemText,
  Paper,
  Box,
} from "@mui/material";

const QuizResults = ({ questions, selectedAnswers, currentQuestion }) => {
  console.log("currentQuestion", currentQuestion);
  return (
    <div>
      <Typography variant="h5">Quiz Results</Typography>
      <List>
        {questions.map((question, questionIndex) => {
          const selectedAnswerIndex = selectedAnswers[questionIndex];
          const correctAnswerIndex = currentQuestion.correct_answer;
          return (
            <ListItem key={questionIndex} style={{ marginBottom: "16px" }}>
              <Paper elevation={3} style={{ padding: "16px", width: "100%" }}>
                <Typography variant="h6">{questionIndex + 1}</Typography>
                <Typography variant="body1">
                  Question: {question.question}
                </Typography>
                <Typography variant="body1">
                  Your Answer: {question.options[selectedAnswerIndex]}
                </Typography>
                <Typography variant="body1">
                  Correct Answer: {question.options[correctAnswerIndex]}
                </Typography>
              </Paper>
            </ListItem>
          );
        })}
      </List>
    </div>
  );
};

export default QuizResults;
