import React, { useState, useEffect } from "react";
import {
  Typography,
  List,
  ListItem,
  ListItemText,
  Radio,
  FormControlLabel,
  Container,
  Button,
  Paper,
  RadioGroup,
  Box,
} from "@mui/material";

import { fetchQuizQuestions } from "../data/api";

const QuizComponent = () => {
  const [questions, setQuestions] = useState([]);
  const [selectedAnswers, setSelectedAnswers] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  useEffect(() => {
    const fetchQuestions = async () => {
      const quizQuestions = await fetchQuizQuestions();
      const initialSelectedAnswers = new Array(quizQuestions.length).fill(null);
      setSelectedAnswers(initialSelectedAnswers);
      setQuestions(quizQuestions);
    };

    fetchQuestions();
  }, []);

  const handleAnswerSelect = (questionIndex, answerIndex) => {
    const newSelectedAnswers = [...selectedAnswers];
    newSelectedAnswers[questionIndex] = answerIndex;
    setSelectedAnswers(newSelectedAnswers);
  };

  const handleQuestionClick = (questionIndex) => {
    setCurrentQuestionIndex(questionIndex);
  };

  const renderOverviewPanel = () => {
    return (
      <div>
        <Typography variant="h5">Questions</Typography>
        <List
          style={{
            display: "flex",
            flexWrap: "wrap",
          }}
        >
          {questions.map((question, questionIndex) => (
            <ListItem
              key={questionIndex}
              onClick={() => handleQuestionClick(questionIndex)}
              button
              selected={currentQuestionIndex === questionIndex}
            >
              {/* <ListItemText
                primary={`No ${questionIndex + 1}`}
                secondary={
                  selectedAnswers[questionIndex] !== null
                    ? "Attempted"
                    : "Not Attempted"
                }
              /> */}
            </ListItem>
          ))}
        </List>
      </div>
    );
  };

  const currentQuestion = questions[currentQuestionIndex];

  return (
    <Container maxWidth="lg">
      <Box display="flex" justifyContent="space-between">
        {/* Overview Panel */}
        <Paper
          elevation={3}
          style={{
            padding: "20px",
            marginRight: "20px",
            width: "20%",
            backgroundColor: "#f0f0f0",
          }}
        >
          {renderOverviewPanel()}
        </Paper>

        <Paper
          elevation={3}
          style={{
            padding: "20px",
            width: "70%",
            height: "fit-content",
            backgroundColor: "#ffffff", // Example background color
          }}
        >
          <Typography variant="h5">Quiz Questions</Typography>
          <List>
            <ListItem>
              <div>
                <ListItemText primary={currentQuestion?.question} />
                <RadioGroup
                  name={`question-${currentQuestionIndex}`}
                  value={selectedAnswers[currentQuestionIndex]}
                  onChange={(e) =>
                    handleAnswerSelect(currentQuestionIndex, e.target.value)
                  }
                >
                  {currentQuestion?.incorrect_answers.map(
                    (choice, choiceIndex) => (
                      <FormControlLabel
                        key={choiceIndex}
                        value={choiceIndex}
                        control={<Radio />}
                        label={choice}
                      />
                    )
                  )}
                </RadioGroup>
              </div>
            </ListItem>
          </List>
        </Paper>
      </Box>

      {/* Submit Button */}
      <Button
        variant="contained"
        color="primary"
        style={{ marginTop: "20px", backgroundColor: "#007bff" }} // Example button style
      >
        Submit Answers
      </Button>
    </Container>
  );
};

export default QuizComponent;
