import React from "react";
import {
  Typography,
  List,
  ListItem,
  ListItemText,
  Paper,
  Box,
} from "@mui/material";

const QuizResults = ({ questions, selectedAnswers }) => {
  return (
    <div>
      <Typography variant="h5">Quiz Results</Typography>
      <List>
        {questions.map((question, questionIndex) => {
          const selectedAnswerIndex = selectedAnswers[questionIndex];
          return (
            <ListItem key={questionIndex} style={{ marginBottom: "16px" }}>
              <Paper elevation={3} style={{ padding: "16px", width: "100%" }}>
                <div style={{ display: "flex", alignItems: "center" }}>
                  <Typography variant="body1">{questionIndex + 1}.</Typography>
                  <Typography variant="body1">{question.question}</Typography>
                </div>
                <Typography variant="body1">
                  Your Answer: {question.options[selectedAnswerIndex]}
                </Typography>
                <Typography variant="body1">
                  Correct Answer: {question.correctAnswer}
                </Typography>
              </Paper>
            </ListItem>
          );
        })}
      </List>
    </div>
  );
};

export default QuizResults;
