import React, { useState } from "react";
import StartPage from "./components/StartPage";
import QuizComponent from "./components/QuizComponent";
import Timer from "./components/Timer";

const App = () => {
  const [showQuiz, setShowQuiz] = useState(false);
  const [email, setEmail] = useState("");
  const [quizLoaded, setQuizLoaded] = useState(false); // Track whether the quiz component has loaded
  const [quizTimerActive, setQuizTimerActive] = useState(false); // Track whether the quiz timer is active

  const startQuiz = (userEmail) => {
    setEmail(userEmail);
    setShowQuiz(true);
    setQuizTimerActive(false); // Start the quiz timer when the quiz starts
  };

  const handleTimeout = () => {
    // Handle auto-submit logic here
    setShowQuiz(false);
    setQuizTimerActive(false); // Stop the quiz timer when it times out
    alert("Quiz time is up! Submitting quiz.");
  };

  const stopQuizTimer = () => {
    setQuizTimerActive(false); // Stop the quiz timer
  };

  const handleQuizLoaded = () => {
    setQuizLoaded(false); // Set quizLoaded to true when the quiz component has loaded
  };

  return (
    <div>
      {!showQuiz ? (
        <StartPage onStart={startQuiz} />
      ) : (
        <>
          <h1>Quiz for {email}</h1>
          {quizTimerActive && (
            <Timer duration={30 * 60} onTimeout={handleTimeout} />
          )}
          <QuizComponent
            onLoaded={handleQuizLoaded}
            stopQuizTimer={stopQuizTimer} // Pass the callback function to stop the timer
          />
          {quizLoaded && quizTimerActive && (
            <Timer duration={30 * 60} onTimeout={handleTimeout} />
          )}
        </>
      )}
    </div>
  );
};

export default App;
