import React, { useState } from "react";
import StartPage from "./components/StartPage";
import QuizComponent from "./components/QuizComponent";
import Timer from "./components/Timer";

const App = () => {
  const [showQuiz, setShowQuiz] = useState(false);
  const [email, setEmail] = useState("");
  const [quizLoaded, setQuizLoaded] = useState(false); // Track whether the quiz component has loaded
  const [quizSubmitted, setQuizSubmitted] = useState(false); // Track whether the quiz has been submitted

  const startQuiz = (userEmail) => {
    setEmail(userEmail);
    setShowQuiz(true);
  };

  const handleTimeout = () => {
    if (!quizSubmitted) {
      // Handle auto-submit logic only if the quiz hasn't been submitted
      setShowQuiz(false);
      alert("Quiz time is up! Submitting quiz.");
    }
  };

  const handleQuizLoaded = () => {
    setQuizLoaded(true); // Set quizLoaded to true when the quiz component has loaded
  };

  const handleQuizSubmit = () => {
    setQuizSubmitted(true);
  };

  return (
    <div>
      {!showQuiz ? (
        <StartPage onStart={startQuiz} />
      ) : (
        <>
          <h1>Quiz for {email}</h1>
          <Timer duration={30 * 60} onTimeout={handleTimeout} />
          <QuizComponent
            onLoaded={handleQuizLoaded}
            onSubmit={handleQuizSubmit}
            quizSubmitted={quizSubmitted}
          />
          {!quizSubmitted && quizLoaded && (
            <Timer duration={30 * 60} onTimeout={handleTimeout} />
          )}
        </>
      )}
    </div>
  );
};

export default App;
