import React from "react";
import { Paper, Typography, Container } from "@mui/material";
import { makeStyles } from "@mui/styles";

const useStyles = makeStyles((theme) => ({
  row: {
    display: "flex",
    alignItems: "center",
    marginBottom: theme.spacing(1),
  },
  item: {
    padding: theme.spacing(2),
    marginRight: theme.spacing(1),
  },
  item1: {
    backgroundColor: "#007bff", // Blue color for item 1
    color: "#fff", // Text color for item 1
  },
  item2: {
    backgroundColor: "#f7a90d", // Yellow color for item 2
  },
  item3: {
    backgroundColor: "#32a852", // Green color for item 3
    color: "#fff", // Text color for item 3
  },
}));

const ColorFul = () => {
  const classes = useStyles();

  return (
    <Container maxWidth="lg">
      <div className={classes.row}>
        <Paper className={`${classes.item} ${classes.item1}`}>
          <Typography variant="body1">Item 1</Typography>
        </Paper>
        <Paper className={`${classes.item} ${classes.item2}`}>
          <Typography variant="body1">Item 2</Typography>
        </Paper>
        <Paper className={`${classes.item} ${classes.item3}`}>
          <Typography variant="body1">Item 3</Typography>
        </Paper>
      </div>
    </Container>
  );
};

export default ColorFul;
