import { Typography, List, ListItem, ListItemText, Stack } from "@mui/material";

const QuestionNo = ({
  questions,
  handleQuestionClick,
  currentQuestionIndex,
  attemptedQuestions,
  visitedQuestions,
}) => {
  return (
    <>
      <div style={(alignItems: "center")}>
        <Typography
          variant="h6"
          style={{ textAlign: "center", marginBottom: "8px" }}
        >
          Questions No.
        </Typography>
        <hr />
        <List style={{ display: "flex", flexWrap: "wrap" }}>
          {questions.map((question, questionIndex) => (
            <ListItem
              key={questionIndex}
              onClick={() => handleQuestionClick(questionIndex)}
              button
              selected={currentQuestionIndex === questionIndex}
              style={{
                width: "50px",
                margin: "4px",
                textAlign: "center",
                borderRadius: "0px 10px 0px 0px",
                backgroundColor: attemptedQuestions.includes(questionIndex)
                  ? "#007bff" // Attempted questions are blue
                  : visitedQuestions.includes(questionIndex)
                  ? "#f7a90d" // Visited questions are yellow
                  : "#00000073", // Not visited questions are white
              }}
            >
              <ListItemText
                primary={`${questionIndex + 1}`}
                style={{
                  color:
                    attemptedQuestions.includes(questionIndex) ||
                    visitedQuestions.includes(questionIndex)
                      ? "#fff" // Text color for attempted or visited questions
                      : "#fff", // Text color for not visited questions
                }}
              />
            </ListItem>
          ))}
        </List>
      </div>
      <hr></hr>
      <Stack direction="row" justifyContent="space-between">
        <div style={{ display: "flex", alignItems: "center" }}>
          <div
            style={{
              width: "20px",
              height: "20px",
              backgroundColor: "#00000073",
              borderRadius: "50%",
              marginRight: "4px",
            }}
          />
          <Typography style={{ fontSize: "16px" }}>Not Visited</Typography>
        </div>
        <div style={{ display: "flex", alignItems: "center" }}>
          <div
            style={{
              width: "20px",
              height: "20px",
              backgroundColor: "#f7a90d",
              borderRadius: "50%",
              marginRight: "6px",
            }}
          />
          <Typography style={{ fontSize: "16px" }}> Visited</Typography>
        </div>
        <div style={{ display: "flex", alignItems: "center" }}>
          <div
            style={{
              width: "20px",
              height: "20px",
              backgroundColor: "#007bff",
              borderRadius: "50%",
              marginRight: "6px",
            }}
          />
          <Typography style={{ fontSize: "16px" }}>Attempted</Typography>
        </div>
      </Stack>
    </>
  );
};

export default QuestionNo;
